func:
state = global.get("state");
global.set('img_counter',0)
global.set('obj_counter',0)
if (state == null){state="free"}

var sleep_before= global.get("custom_sleep_before");
var nb_step= global.get("custom_nb_step");
var nb_frame= global.get("custom_nb_frame");

if (sleep_before === undefined || sleep_before === "" || sleep_before === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Duration before the acquisition";

}else if (nb_step === undefined || nb_step === "" || nb_step === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Number of step in between two frames";

}else if (nb_frame === undefined || nb_frame === "" || nb_frame === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Number of image to save";

}else {
    nb_frame=nb_frame-1

    msg.payload=sleep_before+' '+nb_step+' '+nb_frame;
}
return msg;

var time = new Date(msg.payload);

var hour = time.getUTCHours();
if (hour<10){hour = "0"+hour;}
var minute = time.getUTCMinutes();
if (minute<10){minute = "0"+minute;}

var time_UTC = ""+hour+minute;
global.set('object_time',time_UTC);
return msg;

var date = new Date(msg.payload);

var year = date.getUTCFullYear();
var month = date.getUTCMonth()+1;
if (month<10){month = "0"+month;}
var day = date.getUTCDate();
if (day<10){day = "0"+day;}

var date_UTC = ""+year+month+day;
global.set('object_date',date_UTC);

return msg;

global.set(msg.topic,msg.payload);
var acq_fnumber_objective = String(global.get(msg.topic));

switch(acq_fnumber_objective) {
  case "25":
    global.set("acq_magnification",0.6);
    global.set("process_pixel",1.86);
    global.set("sug_min",60);
    global.set("sug_max",670);
    global.set("sug_flowrate",3);
    break;
  case "16":
    global.set("acq_magnification",0.94);
    global.set("process_pixel",1.19);
    global.set("sug_min",40);
    global.set("sug_max",430);
    global.set("sug_flowrate",2.4);
    break;
  case "12":
    global.set("acq_magnification",1.20);
    global.set("process_pixel",0.94);
    global.set("sug_min",30);
    global.set("sug_max",340);
    global.set("sug_flowrate",1.25);
    break;
  case "8":
    global.set("acq_magnification",1.78);
    global.set("process_pixel",0.63);
    global.set("sug_min",20);
    global.set("sug_max",230);
    global.set("sug_flowrate",0.42);
    break;
  case "6":
    global.set("acq_magnification",2.36);
    global.set("process_pixel",0.48);
    global.set("sug_min",15);
    global.set("sug_max",170);
    global.set("sug_flowrate",0.32);
    break;
}
return msg;

state = global.get("state");

if (state == null){state="free"}

var manual_volume= global.get("pump_manual_volume");
var flowrate= global.get("pump_flowrate");

if (manual_volume === undefined || manual_volume === "" || manual_volume === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Volume to pass";

}else if (flowrate === undefined || flowrate === "" || flowrate === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Flowrate";

}else {
    volume = global.get("pump_manual_volume");
    nb_step=volume*507
    msg.volume = volume;
    flowrate = global.get("pump_flowrate");
    duration=(volume*60)/flowrate
    delay=(duration/nb_step)-0.005
    msg.topic = "actuator/pump";

    if(msg.payload === "FORWARD" & state==="free"){
        msg.payload='FORWARD '+delay+' '+nb_step;
    }
    if(msg.payload === "BACKWARD" & state==="free"){
        msg.payload='BACKWARD '+delay+' '+nb_step;
    }
}
return msg;

state = global.get("state");

if (state == null){state="free"}

var nb_step= global.get("focus_nb_step");

if (nb_step === undefined || nb_step === "" || nb_step === null) {
    msg.topic = "Missing entry :"
    msg.payload = "Number of steps";

}else {
    nb_step= global.get("focus_nb_step");
    if(msg.payload === "UP" & state==="free"){
        msg.payload="FORWARD "+nb_step;
    }
    if(msg.payload === "DOWN" & state==="free"){
        msg.payload="BACKWARD "+nb_step;
    }
}
return msg;

var value = msg.payload;
var key = msg.topic;

global.set(key,value);
return msg;
