function Convertdate_to_string(msg_date) {
    var date = new Date(msg_date)
    return (date.getFullYear() + "/" + fixDigit(date.getMonth()+1) + "/" + fixDigit(date.getDate()) + " " + fixDigit(date.getHours()) + ":" + fixDigit(date.getMinutes()) + ":" + fixDigit(date.getSeconds()) );
}

function fixDigit(val){
    return val.toString().length === 1 ? "0" + val : val;
}

