function ConvertDDToDMS(D, lng){
    // from https://stackoverflow.com/a/5786281/2108279
    var dir = D<0?lng?'W':'S':lng?'E':'N'
    var deg = 0|(D<0?D=-D:D)
    var min = 0|D%1*60
    var sec = (0|D*60%1*6000)/100
    return (deg+ "" +min+ "'" + sec + "\"" + " " + dir)
}

