var ip = "";
var broadcast = "";
var iface = "";
var iface_up = false;

if (msg.rc.code === 0){
    payload = msg.payload
    for (let info in payload) {
      if (payload[info].operstate === "UP") {
      	if (iface_up){
        	// We have two concurrent interfaces up
          // We need to deactivate one
          ip = "Remove Ethernet cable or disconnect wifi"
          broadcast = "Remove Ethernet cable or disconnect wifi"
          iface = "Remove Ethernet cable or disconnect wifi"
          break
        }
        if (payload[info].ifname === "eth0") {
          iface = "Ethernet"
        } else if (payload[info].ifname === "wifi0" || payload[info].ifname === "wlan0") {
          iface = "Wifi"
        }
        ip = payload[info].addr_info[0].local;
        broadcast = payload[info].addr_info[0].broadcast;
        iface_up = true;
      }
    }
}

if (iface_up) {
  return([{
    topic: 'interface',
    payload: iface
  }, {
    topic: 'ip',
    payload: ip
  }, {
    topic: 'broadcast',
    payload: broadcast
  }])
}

