// Code added here will be run once
// whenever the node is started.
function ConvertDDMMToDD(input) {
    // Input Format 3657.4439'N, 1104.2100'W
    // From https://stackoverflow.com/questions/1140189/converting-latitude-and-longitude-to-decimal-values
    if (! input.match(/\d+\d+\.*\d*\'?[NSEW]/)){
    	return "parsing error 1"
    }
    var parts = input.split(/[^\d.]+/)
    if (parts.length < 2){
    	return "parsing error 2"
    }
    var dd = Number(parts[0]) + Number(parts[1])/60
    return dd.toFixed(6) + input.slice(-1)
}

function ValidateCoordinates(input, lat){
    // Input Format 36.574439N, 110.42100W
    // Or 3657.4439'N, 1104.2100'W
    if (input.match(/\d\d/)){
        input = ConvertDDMMToDD(input)
    }

    var error = {}

    if (input.startsWith("parsing error")){
        error.topic = "Error with the " + input
        error.payload = "You need to respect the format example, 36.574439N or 3657.4439'N"
        return [null, error]
    }

    var direction = input.match(/[NSEW]/)
    var position = input.match(/[\+\-\d\.]+/)

    if (direction === null){
        error.topic = "Error with the "
        error.payload = "You need to explicitely enter N/S/E/W"
        return [null, error]
    }

    // Test that position is only made of digits!
    if(/^[\+\-]/.test(position)){
        error.topic = "Error with the "
        error.payload = "Use of +/- sign is inconsistent with N/S/E/W letter! Please only use N/S/E/W!"
        return [null, error]
    }

    var dd = Number(position)
    if (lat){
        // Check latitude
        if (direction == "S" || direction == "N") {
            if (dd>90.0){
                error.topic = "Error with the "
                error.payload = "Latitude is more than 90"
                return [null, error]
            }
        }
        if (direction == "W" || direction == "E") {
            error.topic = "Error with the "
            error.payload = "This is not a Latitude!"
            return [null, error]
        }
    }
    else{
        // Check longitude
        if (direction == "W" || direction == "E") {
            if (dd>180.0){
                error.topic = "Error with the "
                error.payload = "Longitude is more than 180"
                return [null, error]
            }
        }
        if (direction == "N" || direction == "S") {
            error.topic = "Error with the "
            error.payload = "This is not a Longitude!"
            return [null, error]
        }
    }

    if (direction == "S" || direction == "W") {
        dd = dd * -1
    } // Don't do anything for N or E
    return [dd.toFixed(4), null]

}

function ValidateDate(input){
    // Input Format 2020-12-25
    var error = {};

    if (! /20\d{2}-[0-1]\d-[0-3]\d/.test(input)){
        error.topic = "Error with the date";
        error.payload = "The date should respect the ISO format YYYY-MM-DD";
        return [null, error];
    }
    else {
        var date = input.match(/\d+/g);
        if (!((2000 < date[0]) && (date[0] < 2100))){
            error.topic = "Error with the date"
            error.payload = "The year should be between 2000 and 2100"
            return [null, error]
        }
        else if (!((0 < date[1]) && (date[1] <= 12))){
            error.topic = "Error with the date"
            error.payload = "The month should be between 01 and 12"
            return [null, error]
        }
        else if (!((0 < date[2]) && (date[2] <= 31))){
            error.topic = "Error with the date"
            error.payload = "The day should be between 01 and 31"
            return [null, error]
        }
    }
    return [input.replace(/-/g, ''), null]
}

function ValidateTime(input){
    // Input Format 12:00
    var error = {}

    if (! /[0-2]?\d:[0-5]\d/.test(input)){
        error.topic = "Error with the time"
        error.payload = "The date should respect the ISO format HH:MM"
        return [null, error]
    }
    else {
        var time = input.match(/\d+/g)

        if (!((0 <= time[0]) && (time[0] < 24))){
            error.topic = "Error with the time"
            error.payload = "The hours should be 0 and 23."
            return [null, error]
        }
        else if (!((0 <= time[1]) && (time[1] < 60))){
            error.topic = "Error with the time"
            error.payload = "The minutes should be between 0 and 59"
            return [null, error]
        }
    }
    return [input.replace(/:/g, ''), null]
}


