var ellipse = { "name": "WSG84", "a": 6378.137 / 1.852, "invf": 298.257223563 }

function ComputeDistance(lat1, lon1, lat2, lon2) {
    var d, crs12, crs21
    var argacos
    var a, invf

    // lat and lon in radian
    lat1 = (Math.PI / 180) * lat1
    lat2 = (Math.PI / 180) * lat2
    lon1 = (Math.PI / 180) * lon1
    lon2 = (Math.PI / 180) * lon2

    //alert("lat1=" + lat1 + "lon1=" + lon1 +"\nlat2=" +lat2+ "lon2="+lon2)

    /* get distance conversion factor */
    var dc = 1.852 //km
    //alert("dc=" +dc)

    //showProps(ellipse,"ellipse")

    // elliptic code
    d = crsdist_ell(lat1, -lon1, lat2, -lon2, ellipse)  // ellipse uses East negative
    d = d * dc  // go to physical units

    //alert("d="+d+"  crs12="+crs12+"   crs21="+crs21)
    return d
}

function crsdist(lat1, lon1, lat2, lon2) { // radian args
    /* compute course and distance (spherical) */
    if ((lat1 + lat2 === 0) && (Math.abs(lon1 - lon2) == Math.PI) &&
        (Math.abs(lat1) != (Math.PI / 180) * 90)) {
        console.log("Course between antipodal points is undefined")
    }

    var d = Math.acos(Math.sin(lat1) * Math.sin(lat2) + Math.cos(lat1) * Math.cos(lat2) * Math.cos(lon1 - lon2))
    return d
}

function crsdist_ell(glat1, glon1, glat2, glon2, ellipse) {
    // glat1 initial geodetic latitude in radians N positive 
    // glon1 initial geodetic longitude in radians E positive 
    // glat2 final geodetic latitude in radians N positive 
    // glon2 final geodetic longitude in radians E positive 
    var a = ellipse.a
    var f = 1 / ellipse.invf
    //alert("a="+a+" f="+f)
    var r, tu1, tu2, cu1, su1, cu2, s1, b1, f1
    var x, sx, cx, sy, cy, y, sa, c2a, cz, e, c, d
    var EPS = 0.00000000005
    var faz, baz, s
    var iter = 1
    var MAXITER = 100
    if ((glat1 + glat2 === 0) && (Math.abs(glon1 - glon2) == Math.PI)) {
        console.log("Course and distance between antipodal points is undefined")
        glat1 = glat1 + 0.00001 // allow algorithm to complete
    }
    if (glat1 == glat2 && (glon1 == glon2 || Math.abs(Math.abs(glon1 - glon2) - 2 * Math.PI) < EPS)) {
        console.log("Points 1 and 2 are identical- course undefined")
        // @ts-ignore
        var out = new MakeArray(0)
        out.d = 0
        out.crs12 = 0
        out.crs21 = Math.PI
        return out
    }
    r = 1 - f
    tu1 = r * Math.tan(glat1)
    tu2 = r * Math.tan(glat2)
    cu1 = 1 / Math.sqrt(1 + tu1 * tu1)
    su1 = cu1 * tu1
    cu2 = 1 / Math.sqrt(1 + tu2 * tu2)
    s1 = cu1 * cu2
    b1 = s1 * tu2
    f1 = b1 * tu1
    x = glon2 - glon1
    d = x + 1 // force one pass
    while ((Math.abs(d - x) > EPS) && (iter < MAXITER)) {
        iter = iter + 1
        sx = Math.sin(x)
        //       alert("sx="+sx)
        cx = Math.cos(x)
        tu1 = cu2 * sx
        tu2 = b1 - su1 * cu2 * cx
        sy = Math.sqrt(tu1 * tu1 + tu2 * tu2)
        cy = s1 * cx + f1
        y = atan2(sy, cy)
        sa = s1 * sx / sy
        c2a = 1 - sa * sa
        cz = f1 + f1
        if (c2a > 0)
            cz = cy - cz / c2a
        e = cz * cz * 2 - 1
        c = ((-3 * c2a + 4) * f + 4) * c2a * f / 16
        d = x
        x = ((e * cy * c + cz) * sy * c + y) * sa
        x = (1 - c) * x * f + glon2 - glon1
    }
    x = Math.sqrt((1 / (r * r) - 1) * c2a + 1)
    x += 1
    x = (x - 2) / x
    c = 1 - x
    c = (x * x / 4 + 1) / c
    d = (0.375 * x * x - 1) * x
    x = e * cy
    d = ((((sy * sy * 4 - 3) * (1 - e - e) * cz * d / 6 - x) * d / 4 + cz) * sy * d + y) * c * a * r
    if (Math.abs(iter - MAXITER) < EPS) {
        console.log("Algorithm did not converge")
    }
    return d
}


//***************Utility***************
function atan2(y, x) {
    var out
    if (x < 0) { out = Math.atan(y / x) + Math.PI }
    if ((x > 0) && (y >= 0)) { out = Math.atan(y / x) }
    if ((x > 0) && (y < 0)) { out = Math.atan(y / x) + 2 * Math.PI }
    if ((x === 0) && (y > 0)) { out = Math.PI / 2 }
    if ((x === 0) && (y < 0)) { out = 3 * Math.PI / 2 }
    if ((x === 0) && (y === 0)) {
        console.log("atan2(0,0) undefined")
        out = 0
    }
    return out
}

