function ConvertDDToDMS(D, lng){
    // from https://stackoverflow.com/a/5786281/2108279
    return {
        dir : D<0?lng?'W':'S':lng?'E':'N',
        deg : 0|(D<0?D=-D:D),
        min : 0|D%1*60,
        sec :(0|D*60%1*6000)/100
    };
}

