// Explorer Function Node
// TSV  {meta, keys, data: [{id, url, ...keys}]}

const lines = msg.payload.toString().trim().split(/\r?\n/);
if (lines.length < 2) return null;

// 1. Parse Headers
const headers = lines[0].split("\t");
const val = (row, col) => {
    const i = headers.indexOf(col);
    return i >= 0 ? row[i] : null;
};

// 2. Extract Meta from first valid data row
let firstRow = null;
for (let i = 1; i < lines.length; i++) {
    // Skip rows starting with '[' (metadata comments)
    if (!lines[i].trim().startsWith("[")) {
        firstRow = lines[i].split("\t");
        break;
    }
}

// Default meta
const meta = {
    sample_id: "",
    project: "",
    acq_id: "",
    resolution: 1.0 // Default fallback
};

if (firstRow) {
    meta.sample_id = val(firstRow, "sample_id") || "";
    meta.project = val(firstRow, "sample_project") || "";
    meta.acq_id = val(firstRow, "acq_id") || "";

    // CRITICAL: Extract pixel size (microns per pixel)
    // If process_pixel is missing or 0, default to 1 to prevent division by zero
    const px = parseFloat(val(firstRow, "process_pixel"));
    if (!isNaN(px) && px > 0) {
        meta.resolution = px;
    }
}

// 3. Explorer Keys to extract
const explorerKeys = [
    "object_area", "object_equivalent_diameter", "object_perim.",
    "object_major", "object_minor", "object_width", "object_height",
    "object_circ.", "object_elongation", "object_solidity",
    "object_eccentricity", "object_MeanHue", "object_MeanSaturation",
    "object_MeanValue", "object_StdValue"
];

let seq = 0;
const data = [];

// 4. Process Data Lines
for (let i = 1; i < lines.length; i++) {
    const line = lines[i];
    if (line.trim().startsWith("[")) continue;

    const row = line.split("\t");

    const item = {
        id: val(row, "object_id"),
        // Construct URL
        url: `/ps/data/browse/api/preview/big/objects/${val(row,"object_date")}/${val(row,"sample_id")}/${val(row,"acq_id")}/${val(row,"img_file_name")}`,
        sequence_index: seq++
    };

    // Parse numeric keys
    explorerKeys.forEach(k => {
        let v = parseFloat(val(row, k));
        item[k] = isNaN(v) ? 0 : v;
    });

    // Custom calc for greenness (example)
    const hue = item.object_MeanHue || 0;
    const dist = Math.abs(hue - 80);
    item.custom_greenness = Math.max(0, 100 - dist);

    data.push(item);
}

msg.payload = {
    meta,
    keys: explorerKeys,
    data
};

return msg;

// This function clears the plot by sending an empty data array.
// Any template using msg.payload.data will immediately purge the plot.

msg.payload = {
    data: []
};

return msg;

// GALLERY Function Node
// TSV  { data: [], meta: { resolution: ... }, keys: ... }

// 1. Handle Clear Signals
