    msg.payload = { data: [], meta: { resolution: 1 }, keys: [] };
    return msg;
}

// 3. Parse Headers
const headers = lines[0].split('\t');
const get = (row, key) => {
    const idx = headers.indexOf(key);
    return idx >= 0 ? row[idx] : null;
};

// 4. Extract Meta (Resolution is key here)
let firstRow = null;
for (let i = 1; i < lines.length; i++) {
    const L = lines[i].trim();
    if (!L.startsWith('[') && L !== '') {
        firstRow = lines[i].split('\t');
        break;
    }
}

const meta = {
    sample_id: 'N/A',
    project: 'N/A',
    acq_id: 'N/A',
    resolution: 1.0 // Default fallback
};

if (firstRow) {
    meta.sample_id = get(firstRow, 'sample_id') || "";
    meta.project   = get(firstRow, 'sample_project') || "";
    meta.acq_id    = get(firstRow, 'acq_id') || "";

    // CRITICAL: Extract microns per pixel
    const px = parseFloat(get(firstRow, 'process_pixel'));
    if (!isNaN(px) && px > 0) {
        meta.resolution = px;
    }
}

// 5. Select Keys to Display
const usefulKeys = [
    "object_area", "object_width", "object_height",
    "object_equivalent_diameter", "object_major", "object_minor",
    "object_MeanHue", "object_elongation"
];

const data = [];

// 6. Process Rows
for (let i = 1; i < lines.length; i++) {
    const rowLine = lines[i].trim();
    if (rowLine.startsWith('[') || rowLine === '') continue;

    const row = rowLine.split('\t');
    if (row.length !== headers.length) continue;

    const item = {
        id: get(row, 'object_id'),
        // Construct Image URL
        url: `/ps/data/browse/api/preview/big/objects/${get(row, 'object_date')}/${get(row, 'sample_id')}/${get(row, 'acq_id')}/${get(row, 'img_file_name')}`
    };

    // Parse numeric values
    usefulKeys.forEach(k => {
        let v = parseFloat(get(row, k));
        item[k] = isNaN(v) ? 0 : v;
    });

    data.push(item);
}

msg.payload = {
    data,
    keys: usefulKeys,
    meta
};

return msg;

